import React, { Component } from "react";
import General from "./Components/GeneratedGeneral";
import Education from "./Components/GeneratedEducation";
import Experience from "./Components/GeneratedExp";
import style from "./styles/App.css";
import uniqid from 'uniqid';

class App extends Component{
  constructor(){
    super()
    this.state = {
      general:{
        name: '',
        addres:'',
        email:'',
        id: uniqid(),
      },
      generals: [],
    };
  }

  handleChangeName = (e) =>{
    this.setState({
      general:{
        name: e.target.textContent,
        id: this.state.general.id,
      }
    })
  }
  handleChangeAddress = (e) =>{
    this.setState({
      general:{
        addres: e.target.textContent,
      }
    })
  }
  handleChangeEmail = (e) =>{
    this.setState({
      general:{
        email: e.target.textContent,
      }
    })
    console.log(this.general);
  }

  submitAction = (e) =>{
    e.preventDefault();
    this.setState({
      generals: this.state.generals.concat(this.state.general),
      general: {
        name: '',
        addres:'',
        email:'',
      },
    });
    console.log(this.generals);
    console.log(this.general);
  }
  render(){
    const {general, generals} = this.state;
    return(
      <div>
        <h1>CV Generator</h1>
        <div id="content">
          <div id="form-side">
          <form onSubmit={this.submitAction}>
            <h3 htmlFor="General">General Information</h3>
            <label htmlFor="name">Name:</label>
            <input id="name" onChange={this.handleChangeName}></input>
            <label htmlFor="address">Address:</label>
            <input id="address" onChange={this.handleChangeAddress}></input>
            <label htmlFor="email">Email:</label>
            <input id="email" onChange={this.handleChangeEmail}></input>
            <button type="submit" >Generate CV</button>
          </form>
          </div>
          <div id="generated-side">
            <div id="generated-content">
              <General/>
              <Education/>
              <Experience/>
            </div>
          </div>
        </div>
      </div>

    )
  }
}


export default App;
