import React from "react";
import { useState } from "react";
import Education from "./Components/GeneratedEducation";

const EducationInput = () =>{
    const [school, setSchool] = useState('');
    const [subject, setSubject] = useState('');
    const [time, setTime] = useState('');

    const handleChangeSchool = (e) =>{
        setSchool(e.target.value)
        console.log(e.target.value)
    }
    const handleChangeSubject = (e) =>{
        setSubject(e.target.value)
    }
    const handleChangeTime = (e) =>{
        setTime(e.target.value)
    }

    return(
        <div>
            <h3>Education:</h3>
            <label htmlFor="school">School Name:</label>
            <input id="school" value={school} onChange={handleChangeSchool}></input>
            <label htmlFor="subject">Subject:</label>
            <input id="subject" value={subject} onChange={handleChangeSubject}></input>
            <label htmlFor="time">From - To</label>
            <input id="time" value={time} onChange={handleChangeTime}></input>
        </div>
    )
}


export default EducationInput
