import React, { Component } from "react";
import General from "./Components/GeneratedGeneral";
import Education from "./Components/GeneratedEducation";
import Experience from "./Components/GeneratedExp";
import style from "./styles/App.css";
import uniqid from 'uniqid';

class App extends Component{
  constructor(){
    super()

    this.state={
      task: {
        text: '',
        id: uniqid()
    },
      tasks: [],
    };
  }

  handleChange = (e) =>{
    this.setState({
      task:{
        text: e.target.value,
        id: this.state.task.id,
      }
    });
    console.log(this.state.tasks, e.target.textContent)
  };

  submitAct = (e) =>{
    e.preventDefault();
    this.setState({
      tasks: this.state.tasks.concat(this.state.task),
      task: {
        text:"",
        id: uniqid(),
      },
    });
    console.log(this.state.tasks)
  };
  render(){
    const {general, generalok} = this.state;
    return(
      <div>
        <h1>CV Generator</h1>
        <div id="content">
          <div id="form-side">
          <form onSubmit={this.submitAction}>
            <h3 htmlFor="General">General Information</h3>
            <label htmlFor="name">Name:</label>
            <input id="name"></input>
            <label htmlFor="address">Address:</label>
            <input id="address"></input>
            <label htmlFor="email">Email:</label>
            <input id="email" value={general.email} onChange={this.handleChangeEmail}></input>
            <button type="submit" >Generate CV</button>
          </form>
          </div>
          <div id="generated-side">
            <div id="generated-content">
              <General/>
              <Education/>
              <Experience/>
            </div>
          </div>
        </div>
      </div>

    )
  }
}


export default App;
