import React, { Component } from "react";
import General from "./Components/GeneratedGeneral";
import Education from "./Components/GeneratedEducation";
import Experience from "./Components/GeneratedExp";
import style from "./styles/App.css";
import uniqid from 'uniqid';

class App extends Component{
  constructor(){
    super()

    this.state={
      general: {
        name: '',
        email: '',
    },
      generals: [],
    };
  }
  handleChangeName = (e) =>{
    this.setState({
      general:{
        name: e.target.value,
      }
    });
    console.log(this.state.generals, e.target.textContent)
  };

  handleChangeEmail = (e) =>{
    this.setState({
      general:{
        Name: "",
        email: e.target.value,
      }
    });
    console.log(this.state.generals, e.target.textContent)
  };

  submitAction = (e) =>{
    e.preventDefault();
    this.setState({
      generals: this.state.generals.concat(this.state.general),
      general: {
        name: "",
        email: "",
      },
    });
    console.log(this.state.generals)
  };
  render(){
    const {general, generals} = this.state;
    return(
      <div>
        <h1>CV Generator</h1>
        <div id="content">
          <div id="form-side">
          <form onSubmit={this.submitAction}>
            <h3 htmlFor="General">General Information</h3>
            <label htmlFor="name">Name:</label>
            <input id="name" value={general.name} onChange={this.handleChangeName}></input>
            <label htmlFor="address">Address:</label>
            <input id="address"></input>
            <label htmlFor="email">Email:</label>
            <input id="email" value={general.email} onChange={this.handleChangeEmail}></input>
            <button type="submit" >Generate CV</button>
          </form>
          </div>
          <div id="generated-side">
            <div id="generated-content">
              <General/>
              <Education/>
              <Experience/>
            </div>
          </div>
        </div>
      </div>

    )
  }
}


export default App;
